import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
    },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

// export default function InfoPanel() {
export const AllCountries_data = ({country}) => {
    const [GlobalData, setGlobalData] = useState({});
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            const response = await fetch(`https://corona.lmao.ninja/v2/countries/${country}`);
            let data = await response.json();

           
            // delete data.results[0].source;
            console.log(data.results);
            setGlobalData(data);
            

        }
        getData();
    }, [])

    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                {Object.keys(GlobalData).map((key, ind) => {
                    return(
                        <Grid item xs={12} sm={4} key={ind}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>{key.replace(/_/g,' ')}</h3>
                            <h3>{GlobalData[key]}</h3>
                            </Paper>
                        </Grid>
                    )
                })}
                
               

            </Grid>
        </div>
    );
}
