import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

// import { makeStyles } from '@material-ui/core/styles';
import LinearProgress from '@material-ui/core/LinearProgress';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        // maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
        width: 700,
        height: 350,
    },
    progress: {
        width: '100%',
        position: 'relative',
        top: 15,
        height: 20,
        // text: 'center'
      },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    green: {
       color: 'green',
    //    backgroundColor: 'green',
    },
    palette: {
        primary: 'blue',
        secondary: 'pink',
      },
}));

// export default function InfoPanel() {
export const AllCountriesData = ({ country, setcountry }) => {
    const [progress, setProgress] = React.useState(0);
    const [buffer, setBuffer] = React.useState(10);
  
    const progressRef = React.useRef(() => {});
    React.useEffect(() => {
      progressRef.current = () => {
        if (progress > 100) {
          setProgress(0);
          setBuffer(10);
        } else {
          const diff = Math.random() * 10;
          const diff2 = Math.random() * 10;
          setProgress(progress + diff);
          setBuffer(progress + diff + diff2);
        }
      };
    });
  
    React.useEffect(() => {
      const timer = setInterval(() => {
        progressRef.current();
      }, 500);
  
      return () => {
        clearInterval(timer);
      };
    }, []);
    console.log(country);
    const [GlobalData, setGlobalData] = useState({});
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            const response = await fetch(`https://corona.lmao.ninja/v2/countries/${country}`);
            let data = await response.json();
            console.log(data);
            setGlobalData(data);

        }
    getData();
    }, [country])

    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container spacing={4}>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Total Cases</h3>
                            <h3>{GlobalData.cases}</h3>
                            <div className={classes.progress}>
                            <LinearProgress variant="buffer" value={progress} valueBuffer={buffer} color="yellow" style={{backgroundColor: 'green'}}/>
                            </div>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Today Cases</h3>
                            <h3>{GlobalData.todayCases}</h3>
                            <div className={classes.progress}>
                            <LinearProgress variant="buffer" value={progress} valueBuffer={buffer} color="secondary"/>
                            </div>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Total Deaths</h3>
                            <h3>{GlobalData.deaths}</h3>
                            <div className={classes.progress}>
                            <LinearProgress variant="buffer" value={progress} valueBuffer={buffer} />
                            </div>
                            </Paper>
                        </Grid>
                
               

            </Grid>
        </div>
    );
}
