import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import Paper from '@material-ui/core/Paper';
import { NativeSelect, FormControl } from "@material-ui/core";
// import Grid from '@material-ui/core/Grid';
import { AllCountries_data } from './AllCountries_data'


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
    },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

// export default function InfoPanel() {
export const AllCountries = () => {
    const [country, setcountry] = useState(["pakistan"])
    const [globalData, setglobalData] = useState([{}]);
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            // const response = await fetch("https://api.thevirustracker.com/free-api?countryTotals=ALL");
            const response = await fetch("https://corona.lmao.ninja/v2/countries/");
            
            let data = await response.json();

            console.log(data);
            console.log(data[0].country);
            // delete data.results[0].source;
            // setglobalData(Object.values(data.countryitems[0]));
            setglobalData(data);
            // console.log(data.countryitems[0][1]);

        }
        getData();
    }, [setglobalData])

    const classes = useStyles();

    return (
        <FormControl  className={classes.root}>
            <h1>Curretn county is {country}</h1>
            <AllCountries_data country={country} />
            <NativeSelect id="select" onChange={(e)=> setcountry(e.target.value)}>
                console.log(globalData);
            <option value="">{country}</option>
                {Object.keys(globalData).map((key, ind) => {
                    return(
                  <option key={ind} value={globalData[key].country}>{globalData[key].country}</option>
                    )
                })}
                
               
                </NativeSelect>
        </FormControl>
    );
}
