import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

// import { makeStyles } from '@material-ui/core/styles';
import LinearProgress from '@material-ui/core/LinearProgress';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        // maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
        width: 700,
        height: 350,
    },
    progress: {
        width: '100%',
        position: 'relative',
        top: 15,
        height: 20,
        // text: 'center'
      },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    green: {
       color: 'green',
    //    backgroundColor: 'green',
    },
    palette: {
        primary: 'blue',
        secondary: 'pink',
      },
      img : {
        width: '5rem',
      },
      bottom: {
        borderBottom: '10px solid rgba(0, 255, 0, 0.5)', 
      }
}));


// export default function InfoPanel() {
export const AllCountriesData = ({ country, setcountry }) => {
    
    console.log(country);
    const [GlobalData, setGlobalData] = useState({});
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            const response = await fetch(`https://corona.lmao.ninja/v2/countries/${country}`);
            let data = await response.json();
            console.log(data);
            setGlobalData(data);

        }
    getData();
    }, [country])

    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container spacing={4} >
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <img src="http://covid19-virus-tracker.surge.sh/static/media/infected.4abefb64.svg" alt="covid-19" className={classes.img}/>
                            <h3 className={classes.title}>Total Cases</h3>
                            <h3>{GlobalData.cases}</h3>
                            <div className={classes.progress}>
                             
                            <div className={classes.bottom}></div>
                            </div>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}>
                              <img src="http://covid19-virus-tracker.surge.sh/static/media/recovered.05cc8e67.svg" alt="covid-19" className={classes.img}/>
                             
                            <h3 className={classes.title}>Today Cases</h3>
                            <h3>{GlobalData.todayCases}</h3>
                            <div className={classes.progress}>
                            <LinearProgress variant="determinate" color="secondary"/>
                            </div>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <img src="http://covid19-virus-tracker.surge.sh/static/media/deaths.df846e3e.svg" alt="covid-19" className={classes.img}/>
                            
                            <h3 className={classes.title}>Total Deaths</h3>
                            <h3>{GlobalData.deaths}</h3>
                            <div className={classes.progress}>
                            <LinearProgress variant="determinate" />
                            </div>
                            </Paper>
                        </Grid>
                
               

            </Grid>
        </div>
    );
}









