import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
        width: 400,
    },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

// export default function InfoPanel() {
export const AllCountriesData = ({ country, setcountry }) => {
    console.log(country);
    const [GlobalData, setGlobalData] = useState({});
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            const response = await fetch(`https://corona.lmao.ninja/v2/countries/${country}`);
            let data = await response.json();
            console.log(data);
            setGlobalData(data);

        }
    getData();
    }, [country])

    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container spacing={4}>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Total Cases</h3>
                            <h3>{GlobalData.cases}</h3>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Today Cases</h3>
                            <h3>{GlobalData.todayCases}</h3>
                            </Paper>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Paper className={classes.paper} elevation={3}> 
                            <h3 className={classes.title}>Total Deaths</h3>
                            <h3>{GlobalData.deaths}</h3>
                            </Paper>
                        </Grid>
                
               

            </Grid>
        </div>
    );
}
