import React, { useEffect, useState } from 'react';
import {Bar,Line } from 'react-chartjs-2';



export const Chart = ({data: { confirmed, recovered, deaths }, countryData, country})=>{
  
  // displayName: 'BarExample';

  console.log(country);
  const [dailyData, setDailyData] = useState({});
  // const [GlobalData, setGlobalData] = useState({});
  // it takes 2 thing = 1st is callback function and 2nd is array
  useEffect(() => {
      async function getData() {
        try {
          const response = await fetch(`https://corona.lmao.ninja/v2/countries/${country}`);
          const alldata = await response.json();
          console.log(alldata);
          const data = alldata.map((dailyData) => ({
          confirmed: dailyData.cases,
          deaths: dailyData.deaths,
          recovered: dailyData.recovered,
        }));
      
        setDailyData(data);
      

      }
      catch (error) {
        console.log(error);
      }
    }
  getData();
  }, [country])


  const LineChart = dailyData.length ? (
    <Line
      data={{
        labels: dailyData.map(({ date }) => date),
        datasets: [
          {
            data: dailyData.map(({ confirmed }) => confirmed),
            label: "Infected",
            borderColor: "#3333ff",
            fill: true,
          },
          {
            data: dailyData.map(({ deaths }) => deaths),
            label: "Deaths",
            borderColor: "red",
            backgroundColor: "rgba(255, 0, 0, 0.5)",
            fill: true,
          },
        ],
      }}
    />
  ) : null;

  // Barchart
  const Barchart = confirmed ? (
    <Bar
      data={{
        labels: ["Infected", "Recovered", "Deaths"],
        datasets: [
          {
            label: "People",
            backgroundColor: [
              "rgba(0, 0, 255, 0.5)",
              "rgba(0, 255, 0, 0.5)",
              "rgba(255, 0, 0, 0.5)",
            ],
            data: [confirmed.value, recovered.value, deaths.value],
          },
        ],
      }}
      options={{
        legend: { display: false },
        title: { display: true, text: `Current State in ${countryData}` },
      }}
    />
  ) : null;

  // render() {
    return (
      <div className={"chart-container"}>
      {countryData ? Barchart : LineChart}
    </div>
      // <div>
      //   <h2>Bar Example (custom size)</h2>
      //   <Bar
      //     data={data}
      //     width={100}
      //     height={50}
      //     options={{
      //       maintainAspectRatio: false
      //     }}
      //   />
      // </div>
    );
  // }
}

