import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import Paper from '@material-ui/core/Paper';
import NativeSelect from '@material-ui/core/NativeSelect';
// import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        maxWidth: 1000,
        margin: '0 auto',
        marginTop: 50,
    },
    title: {
    color: '#3f51b5',
    text: 'uppercase',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

// export default function InfoPanel() {
export const AllCountries = () => {
    const [globalData, setglobalData] = useState([{}]);
    // it takes 2 thing = 1st is callback function and 2nd is array
    useEffect(() => {
        async function getData() {
            // const response = await fetch("https://api.thevirustracker.com/free-api?countryTotals=ALL");
            const response = await fetch("https://corona.lmao.ninja/v2/countries/");
            
            let data = await response.json();

            // console.log(data);
            // delete data.results[0].source;
            // setglobalData(Object.values(data.countryitems[0]));
            setglobalData(data);
            // console.log(data.countryitems[0][1]);

        }
        getData();
    }, [])

    const classes = useStyles();

    return (
        <div className={classes.root}>
            {/* <Grid container spacing={3}> */}
            <NativeSelect id="select">
            <option value="global">Global</option>
                {Object.keys(globalData).map((key, ind) => {
                    return(
                        // <Grid item xs={12} sm={4} key={ind}>
                        //     <Paper className={classes.paper} elevation={3}> 
                        //     <h3 className={classes.title}>{key.replace(/_/g,' ').toUpperCase()}</h3>
                        //     <h3>{globalData[0][key]}</h3>
                        //     </Paper>
                        // </Grid>
                        
                        
                        <option key={ind} value={globalData.country}>{globalData.country}</option>
                        
                    )
                })}
                
               
                </NativeSelect>
            {/* </Grid> */}
        </div>
    );
}
